import React from 'react'
import styled from '@emotion/styled'
import Input from './components'
import { getGames } from './api'

const Container = styled.div`
  width: 100%;
`

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      games: []
    }
  }

  search = () => {
    this.setState({
      games: getGames()
    })
  }

  render() {
    this.search()
    return (
      <Container>
        <Input onSearch={this.search} />
      </Container>
    )
  }
}

export default App
