import React from 'react'
import styled from '@emotion/styled'
import Input from './components'
import { getGames } from './api'

const Container = styled.div`
  width: 100%;
`

class App extends React.Component {
  getGames = () => {
    this.setState({
      games: getGames('V75')
    })
  }

  render() {
    return (
      <Container>
        <Input onSearch={() => this.getGames()} />
      </Container>
    )
  }
}

export default App
